                {:val 10   :one \X :five \L}
                {:val 1    :one \I :five \V}),
       fillCh (fn[val entry pre]
                 (let[one (:one entry),
                      five (:five entry),
                      n (quot val (:val entry) )]
                 (cond (= n 0)
                        nil
                       (< n 4)
                       (replicate n one)
                       (= n 4)
                       (vector one five)
                       (= n 5)
                        five                       
                       (< n 9)
                       (conj (replicate (- n 5) one) five)                       :else 
                       (vector one pre)
                    )
                   )
                 )
                 ]
    (loop[val num,numCol chmap,result [],prefix nil]
      (let[currInfo (first numCol),
           currNum (:val currInfo)]
       (if (empty? numCol)
           (apply str result)
        (do
         (println (str "val:" num " currNum:" currNum ) ) 
	       (recur  (rem val currNum)
	               (rest numCol)
	               (into result (fillCh val currInfo prefix) )
                 (:one currInfo)
	            )
	        )
         )
        )
      )
    
   )
