(let [rn-table '[{:M nil       :C nil  :X nil  :I nil  }
                   {:M M         :C C    :X X    :I I    }
                   {:M MM        :C CC   :X XX   :I II   }
                   {:M MMM       :C CCC  :X XXX  :I III  }
