(fn [ind_ lis_] 

  (let [lis (vec lis_)
        ind (cond (>= (Math/abs ind_) (count lis))
          (cond (> 0 ind_)
                (+ (count lis) ind_)
                :else (- ind_ (count lis))
          )
          :else ind_
    )]
    (cond (> 0 ind)
         (concat 
           (subvec lis (+ (count lis) ind) (count lis))
           (drop-last (Math/abs ind) lis)
         )
         :else
         (concat
           (drop ind lis)
           (subvec lis 0 ind)
         )
    )
  )
)
