(fn word_chains [ss]
    (let
    [
        diff_by_insert_or_delete_helper?
            (fn [w1 w2 index1 index2 delta] {:pre (= (- (count w2) (count w1)) 1)}
                (let [c1 (get w1 index1) c2 (get w2 index2)]
                    (cond
                        (and (>= index1 (count w1)) (>= index2 (count w2))) (= delta 1) ; reached the ends of both strings
                        (= c1 c2) (recur w1 w2 (inc index1) (inc index2) delta) ; strings are the same, continue
                        (= delta 0) (recur w1 w2 index1 (inc index2) (inc delta)) ; first difference in strings, continue assuming deletion of the larger word
                        (> delta 0) false ; more than one difference detected
                    )
                )
            )
        diff_by_insert_or_delete?
            (fn [w1 w2]
                (let
                [ [w1 w2] (sort-by count [w1 w2]) ]
                (cond
                    (not= (- (count w2) (count w1)) 1) false
                    :otherwise (diff_by_insert_or_delete_helper? w1 w2 0 0 0)
                )
                )
            )
        diff_by_sub_helper?
            (fn [w1 w2 index delta] {:pre (= (count w2) (count w1))}
                (let [c1 (get w1 index) c2 (get w2 index)]
                    (cond
                        (>= index (count w1)) (= delta 1) ; reached the ends of both strings
                        (= c1 c2) (recur w1 w2 (inc index) delta) ; strings are the same, continue
                        (= delta 0) (recur w1 w2 (inc index) (inc delta)) ; first difference in strings, continue assuming a substitution
                        (> delta 0) false ; more than one difference detected
                    )
                )
            )
        diff_by_sub?
            (fn [w1 w2]
                (cond
                    (not= (count w2) (count w1)) false
                    :otherwise (diff_by_sub_helper? w1 w2 0 0)
                )
            )
        edit_distance_one?
            (fn [w1 w2] (or (diff_by_insert_or_delete? w1 w2) (diff_by_sub? w1 w2)) )
        hamiltonian_helper?
            (fn f [[g chain total] curr] {:pre (set? chain)}
                (cond
                    (= total (count chain)) true
                    (chain curr) false
                    :otherwise (some identity (map
                        #(f [g (conj chain curr) total] %)
                        (g curr)
                    ))
                )
            )
        hamiltonian?
            (fn [g] (true? (some identity (map (fn [k] (hamiltonian_helper? [g #{} (count g)] k)) (keys g)))))
        generate_adjacency_map
            (fn [ss pred] {:pre (fn? pred)}
                (reduce (fn [ret k] (assoc ret k (into [] (filter #(pred k %) ss)))) {} ss)
            )
    ]
    (hamiltonian? (generate_adjacency_map ss edit_distance_one?))
    )
