              )
        neighbors (fn [row col]
                    (for [r (range (max 0 (dec row))
                                   (min (+ 2 row) (:row bound)))
                          c (range (max 0 (dec col))
                                   (min (+ 2 col) (:col bound)))
                          :when (not= [row col] [r c])
