        alive? (fn [cll] (= cll ALIVE))
        cell-lives? (fn [cell-alive? alive-neighbors-count]
                   (or
                    (= alive-neighbors-count 3)
                    (and
                     cell-alive? (= alive-neighbors-count 2))
                   )

                   )
        next-val (fn [row col]
                   (let [cell-alive? (alive? (cell row col))
                         alive-neighbors-count (count (filter alive? (neighbors row col)))
                         ]
                     (if (cell-lives? cell-alive? alive-neighbors-count)
                       ALIVE
